			const val = event.target.value;
			setValue(this.vm, expr, val);
		});
	}
	// v-on
	on(node, type, expr) {
		const method = getValue(this.vm, expr);
		node.addEventListener(type, method);
	}
	// v-bind
	bind(node, type, expr) {
        node.setAttribute(type, getValue(this.vm, expr)||expr);
        node.removeAttribute(':' + type);
	}
